import React, { Component } from 'react';
import axios from 'axios';

export default class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      posts: [],
    };
  }
  componentDidMount() {
    this.retrievePosts();
  }
  retrievePosts() {
    axios.get('http://localhost:8000/posts').then((res) => {
      if (res.data.success) {
        this.setState({
          posts: res.data.existingPosts,
        });
        console.log(this.state.posts);
      }
    });
  }
  render() {
    return (
      <div>
        <p>All Equipment</p>
        {this.state.posts.map((posts) => (
          <div>
            <p>{posts.equipmentName}</p>
            <p>{posts.supplyCompany}</p>
            <p>{posts.stock}</p>
            <p>{posts.priceIndollars}</p>
            <p>{posts.ageInyears}</p>
            <p>{posts.description}</p>
          </div>
        ))}
      </div>
    );
  }
}
